function Product() {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
  let componentmounted = true;

  useEffect(() => { 
    const getproduct = async () => {
 setLoading(true);
const response = await fetch("https://fakestoreapi.com/products");
if(componentmounted){
  setData(await response.clone().json());
  setFilter(await response.json());
  setLoading(false);
  console.log(filter)
}

    return () => {
      componentmounted = false;
    }
    }
    getproduct();
  },[]);

  const Loading = () =>{
    return(
      <div>Loading...</div>
      
    )
  } 
  const ShowProducts = () => {
    return(<div className='d-flex  justify-content-center mb-5 pb-5'>
    <div className='buttons'>
<button className='btn btn-outline-dark me-2'>all</button>
<button className='btn btn-outline-dark me-2'>Mens clothing</button>
<button className='btn btn-outline-dark me-2'>Womens clothing </button>
<button className='btn btn-outline-dark me-2'>jewelery</button>
<button className='btn btn-outline-dark me-2'>Electronics</button>
</div>


{filter.map((product) => {
  return(
  <>
  <div className='col-md-3 ro mb-4'>
  <div class="card h-100 text-center p-4 me-2 "key={product.id}> 
  <img src={product.image} class="card-img-top" alt={product.title} height ="350px" />
  <div class="card-body">
    <h5 class="card-title text-center mb-0">{product.title.substring(0,12)}</h5>
    <p class="card-text">â‚¹{product.price}</p>
    <a href="#" class="btn btn-outline-dark">BUY Now</a>
  </div>
</div>
  </div>
  </>)
})}
</div> );
  };
  
  return (
    <div>
      <div className='container my-5 py-5'>
<div className='row'>
  <div className='col-12 mb-5'> 
<h1 className='display-6 fw-bolder text-center'>Latest Product</h1>
<hr/>
  </div>
</div>
<div className='d-flex justify-content-center'>
{loading ? <Loading />: <ShowProducts/>}
</div>
      </div>
    </div>
  )
}

