import React,{useState,useEffect} from 'react'

function Product() {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
  let componentmounted = true;

  useEffect(() => { 
    const getproduct = async () => {
 setLoading(true);
const response = await fetch("https://fakestoreapi.com/products");
if(componentmounted){
  setData(await response.clone().json());
  setFilter(await response.json());
  setLoading(false);
  console.log(filter)
}

    return () => {
      componentmounted = false;
    }
    }
    getproduct();
  },[]);

  const Loading = () =>{
    return(
      <div className='d-flex justify-content-center'>
        <div className='spinner-border text-primary' role='status'>
        <span className='sr-only'>Loading...</span>
        </div>
      </div>
      
    )
  } 
  const ShowProducts = () => {
    return(<div className=' me-2 mb-5 pb-7 '>
    <div className='buttons d-flex  justify-content-center mb-5 pb-5 '>
<button className='btn btn-outline-dark mb-2 me-3 text-center'>All</button>
<button className='btn btn-outline-dark mb-2 me-3 text-center'>Mens clothing</button>
<button className='btn btn-outline-dark mb-2 me-3 text-center'>Womens clothing </button>
<button className='btn btn-outline-dark mb-2 me-3 text-center'>Jewelery</button>
<button className='btn btn-outline-dark mb-2 me-3 text-center'>Electronics</button>
</div>
 


<div className='row'>
{filter.map((product) => {
  return(
  
  <div className='col-md-4  mb-4'>
  <div class="card h-100 text-center p-4 me-2 "key={product.id}> 
  <img src={product.image} class="card-img-top" alt={product.title} height ="250px" />
  <div class="card-body">
    <h5 class="card-title text-center mb-0">{product.title.substring(0,12)}</h5>
    <p class="card-text">₹{product.price}</p>
    <a href="#" class="btn btn-primary">BUY Now</a>
  </div>
</div>
  </div>
  )
})} </div>

</div> 
)}
  return (
    <div>
      <div className='container my-5 py-5'>
<div className='row'>
  <div className='col-12 mb-5'> 
<h1 className='display-6 fw-bolder text-center'>Latest Products</h1>
<hr/>
  </div>
</div>
<div className='d-flex justify-content-center'>
{loading ? <Loading />: <ShowProducts/>}
</div>
      </div>
    </div>
  )
}

export default Product
