import React,{useState,useEffect} from 'react'

function Product() {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
  let componentmounted = true;

  useEffect(() => { 
    const getproduct = async () => {
 setLoading(true);
const response = await fetch("https://fakestoreapi.com/products");
if(componentmounted){
  setData(await response.json());
  setFilter(await response.json());
  setLoading(false);
  console.log(filter)
}

    return () => {
      componentmounted = false;
    }
    }
    getproduct();
  },[]);

  const Loading = () =>{
    return(
      <div>Loading...</div>
      
    )
  } 
  const ShowProducts = () => {
    return(<div className='d-flex justify-content-center mb-5 pb-5'>
    <div className='buttons'>
<button className='btn btn-outline-dark me-2'>all</button>
<button className='btn btn-outline-dark'>Mens clothing</button>
<button className='btn btn-outline-dark'>Womens clothing </button>
<button className='btn btn-outline-dark'>jewelary</button>
<button className='btn btn-outline-dark'>Electronics</button>
</div>
</div> );
  };

  return (
    <div>
      <div className='container my-5 py-5'>
<div className='row'>
  <div className='col-12 mb-5'> 
<h1 className='display-6 fw-bolder text-center'>Latest Product</h1>
<hr/>
  </div>
</div>
<div className='d-flex justify-content-center'>
{loading ? <Loading />: <ShowProducts/>}
</div>
      </div>
    </div>
  )
}

export default Product
